    price: number,
    saleTypeIds:array,
    address: string,
    city:string,
    provinceId: string,
    squareMeter: number,
    rooms: number,
    bathrooms: number,
    locationUrl: string,
    mainFeatures: array de string,
    equipmentIds:array string,
    images:array string*/

export const formValidation = createFormValidation(validationSchema);
